import React,{useState} from 'react'

export default function TextForm(props) {
    const handleUpCase = ()=>{
        console.log("Uppercase button was clicked");
        let newText = text.toUpperCase();
        setText(newText);
    }
    const handleLoCase = ()=>{
        console.log("Uppercase button was clicked");
        let newText = text.toLowerCase();
        setText(newText);
    }
    const handleOnChange = (event)=>{
        console.log("On change");
        setText(event.target.value);

    }
    const [text,setText] = useState('Enter the text');
    return (
        <>
            <div className="mb-3">
                <h1>{props.heading}</h1>
                <textarea className="form-control" value = {text}  onChange={handleOnChange}  id="myBox" rows="10"></textarea>
            </div>  
            <button className="btn btn-primary mx-2" onClick={handleUpCase}>Convert to Uppercaser</button>
            <button className="btn btn-primary mx-2" onClick={handleLoCase}>Convert to LowerCase</button>
            <div className="container my-3">
                <h2>Your Text Summary</h2>
                <p>{text.split(" ").length} words and {text.length} characters</p>
                <p>{0.008*text.split(" ").length} minutes to read these words</p>
                <h2>Preview</h2>
                <p>{text}</p>

            </div>
    </>
  )
}
